mapper.select:
select
  id, isbn, name, sub_name, origin_name, publisher_id, producer_id, book_group_id, bind_id, paper_id, publish_time, publish_batch, print_time, print_batch, page_count, word_count, score, price, size_length, size_width, print_sheet, language, fit_age_min, fit_age_max, create_time, update_time, del_flag
from meta_book


        and id = #{metaBook.id}


        and isbn = #{metaBook.isbn}


        and name = #{metaBook.name}


        and sub_name = #{metaBook.subName}


        and origin_name = #{metaBook.originName}


        and publisher_id = #{metaBook.publisherId}


        and producer_id = #{metaBook.producerId}


        and book_group_id = #{metaBook.bookGroupId}


        and bind_id = #{metaBook.bindId}


        and paper_id = #{metaBook.paperId}


        and publish_time = #{metaBook.publishTime}


        and publish_batch = #{metaBook.publishBatch}


        and print_time = #{metaBook.printTime}


        and print_batch = #{metaBook.printBatch}


        and page_count = #{metaBook.pageCount}


        and word_count = #{metaBook.wordCount}


        and score = #{metaBook.score}


        and price = #{metaBook.price}


        and size_length = #{metaBook.sizeLength}


        and size_width = #{metaBook.sizeWidth}


        and print_sheet = #{metaBook.printSheet}


        and language = #{metaBook.language}


        and fit_age_min = #{metaBook.fitAgeMin}


        and fit_age_max = #{metaBook.fitAgeMax}


        and create_time = #{metaBook.createTime}


        and update_time = #{metaBook.updateTime}


        and del_flag = #{metaBook.delFlag}

    and del_flag = 0

limit #{limitIndex}, #{limitSize}

select count(1)
from meta_book


        and id = #{id}


        and isbn = #{isbn}


        and name = #{name}


        and sub_name = #{subName}


        and origin_name = #{originName}


        and publisher_id = #{publisherId}


        and producer_id = #{producerId}


        and book_group_id = #{bookGroupId}


        and bind_id = #{bindId}


        and paper_id = #{paperId}


        and publish_time = #{publishTime}


        and publish_batch = #{publishBatch}


        and print_time = #{printTime}


        and print_batch = #{printBatch}


        and page_count = #{pageCount}


        and word_count = #{wordCount}


        and score = #{score}


        and price = #{price}


        and size_length = #{sizeLength}


        and size_width = #{sizeWidth}


        and print_sheet = #{printSheet}


        and language = #{language}


        and fit_age_min = #{fitAgeMin}


        and fit_age_max = #{fitAgeMax}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}

    and del_flag = 0
