mapper.select:
select
  id, label_cate_id, name, status, create_time, update_time, del_flag, descr, seq
from meta_label


        and id = #{metaLabel.id}


        and label_cate_id = #{metaLabel.labelCateId}


        and name = #{metaLabel.name}


        and status = #{metaLabel.status}


        and create_time = #{metaLabel.createTime}


        and update_time = #{metaLabel.updateTime}


        and del_flag = #{metaLabel.delFlag}


        and descr = #{metaLabel.descr}


        and seq = #{metaLabel.seq}

    and del_flag = 0

limit #{limitIndex}, #{limitSize}

select count(1)
from meta_label


        and id = #{id}


        and label_cate_id = #{labelCateId}


        and name = #{name}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}


        and descr = #{descr}


        and seq = #{seq}

    and del_flag = 0
